mapper.update:
UPDATE reply
   SET writer = #{writer}
      ,content = #{content}
      ,group_no = #{groupNo}
      ,parent_no = #{parentNo}
      ,depth_no = #{depthNo}
      ,seq_no = #{seqNo}
      ,upd_date = now()
WHERE reply_no = #{replyNo}

UPDATE reply
   SET seq_no = seq_no + 1
 WHERE group_no = #{groupNo}
   AND seq_no >= #{seqNo}

UPDATE reply
   SET sub_count = sub_count + #{no}
 WHERE reply_no = #{parentNo}
